package projecteuler;

public class Problem8 {

	public static long stringParser(String a){
		long total = 1; 
		for (int i=0; i<a.length(); i++){
			total*=Integer.parseInt(String.valueOf(a.charAt(i)));
		}
		return total; 
	}
	
	public static long largestProduct(String a, int b){
		long largest = 0; 
		for (int i=0; i<a.length()-b; i++){
			long temp = stringParser(a.substring(i, i+b));
			if (temp>largest){
				largest=temp;
			}
		}
		return largest;
	}
	
	public static void main(String[] args){
		System.out.println(largestProduct("", 13));
	}
	
}
